import { Component, Inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { combineLatest } from 'rxjs';
import {
  EmployeeListDto,
  DepartmentDto,
  EmployeeClient,
  DepartmentClient,
  EmployeeUpdateDto,
} from 'src/app/api/app.generated';
import { EmployeeAddComponent } from '../employee-add/employee-add.component';
import { EmployeeDetailsDto } from 'src/app/core/api/app.generated';

@Component({
  selector: 'app-employee-edit',
  templateUrl: './employee-edit.component.html',
  styleUrls: ['./employee-edit.component.css'],
})
export class EmployeeEditComponent {
  editEmployeeForm!: FormGroup;
  bossSelectOptions: EmployeeListDto[] = [];
  departmentSelectOptions: DepartmentDto[] = [];
  hide = true;
  dataLoaded = false;
  employeeId!: number;
  employeeData!: EmployeeDetailsDto;

  constructor(
    private readonly employeeClient: EmployeeClient,
    private readonly departmentClient: DepartmentClient,
    private readonly dialogRef: MatDialogRef<EmployeeAddComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any,
    private readonly fb: FormBuilder
  ) {
    this.employeeId = data.id;
  }

  ngOnInit(): void {
    combineLatest([
      this.employeeClient.employeeAll(),
      this.departmentClient.departmentAll(),
      this.employeeClient.employeeGET(this.employeeId),
    ]).subscribe((result) => {
      this.dataLoaded = true;

      // Saját maga ne lehessen a főnöke
      (this.bossSelectOptions = result[0].filter(
        (x) => x.id !== this.employeeId
      )),
        (this.departmentSelectOptions = result[1]);
      this.employeeData = result[2];
      this.initForm();
    });
  }

  initForm() {
    this.editEmployeeForm = this.fb.group({
      name: [
        this.employeeData.name,
        [Validators.required, Validators.maxLength(100)],
      ],
      position: [
        this.employeeData.position,
        [Validators.required, Validators.maxLength(100)],
      ],
      phone: [
        this.employeeData.phone,
        [Validators.required, Validators.maxLength(16)],
      ],
      bossId: [this.employeeData.bossId ?? null],
      departmentId: [this.employeeData.departmentId, [Validators.required]],
    });
  }

  cancel() {
    this.dialogRef.close(false);
  }

  submitForm() {
    this.employeeClient
      .employeePUT(this.employeeId, {
        name: this.editEmployeeForm.controls['name'].value,
        position: this.editEmployeeForm.controls['position'].value,
        phone: this.editEmployeeForm.controls['phone'].value,
        bossId: this.editEmployeeForm.controls['bossId'].value,
        departmentId: this.editEmployeeForm.controls['departmentId'].value,
      } as EmployeeUpdateDto)
      .subscribe(() => {
        this.dialogRef.close(true);
      });
  }

  public errorHandling = (control: string, error: string) => {
    return this.editEmployeeForm.controls[control].hasError(error);
  };
}
<div class="container">
  <ng-container *ngIf="dataLoaded">
    <h3 mat-dialog-title>Edit employee data</h3>

    <form [formGroup]="editEmployeeForm">
      <div class="content-container">
        <mat-form-field appearance="fill">
          <mat-label>Name</mat-label>
          <input matInput placeholder="Name" formControlName="name" />
          <mat-error *ngIf="errorHandling('name', 'required')">
            Name is required
          </mat-error>
          <mat-error *ngIf="errorHandling('name', 'maxlength')">
            Name can't be longer than 100 characters
          </mat-error>
        </mat-form-field>

        <mat-form-field appearance="fill">
          <mat-label>Position</mat-label>
          <input matInput placeholder="Position" formControlName="position" />
          <mat-error *ngIf="errorHandling('position', 'required')">
            Position is required
          </mat-error>
          <mat-error *ngIf="errorHandling('position', 'maxlength')">
            Position can't be longer than 100 characters
          </mat-error>
        </mat-form-field>

        <mat-form-field appearance="fill">
          <mat-label>Phone</mat-label>
          <span matPrefix> +36 &nbsp;</span>
          <input
            matInput
            type="tel"
            placeholder="20-123-4567"
            formControlName="phone"
          />
          <mat-error *ngIf="errorHandling('phone', 'required')">
            Phone is required
          </mat-error>
          <mat-error *ngIf="errorHandling('phone', 'maxlength')">
            Phone can't be longer than 16 characters
          </mat-error>
        </mat-form-field>

        <mat-form-field>
          <mat-label>Boss</mat-label>
          <mat-select formControlName="bossId">
            <mat-option
              *ngFor="let boss of bossSelectOptions"
              [value]="boss.id"
            >
              {{ boss.name }}
            </mat-option>
          </mat-select>
        </mat-form-field>

        <mat-form-field>
          <mat-label>Department</mat-label>
          <mat-select formControlName="departmentId">
            <mat-option
              *ngFor="let department of departmentSelectOptions"
              [value]="department.id"
            >
              {{ department.name }}
            </mat-option>
          </mat-select>
        </mat-form-field>

        <div mat-dialog-actions>
          <button
            mat-raised-button
            type="submit"
            color="primary"
            [mat-dialog-close]="1"
            [disabled]="!editEmployeeForm.valid"
            (click)="submitForm()"
          >
            Save
          </button>
          <button mat-button (click)="cancel()" tabindex="-1">Cancel</button>
        </div>
      </div>
    </form>
  </ng-container>

  <ng-container *ngIf="!dataLoaded">
    <div class="spinner-container"><mat-spinner></mat-spinner></div>
  </ng-container>
</div>
