import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import {
  DepartmentDto,
  EmployeeClient,
  EmployeeListDto,
} from 'src/app/api/app.generated';

@Component({
  selector: 'app-employee-add',
  templateUrl: './employee-add.component.html',
  styleUrls: ['./employee-add.component.css'],
})
export class EmployeeAddComponent {
  addEmployeeForm!: FormGroup;

  bossSelectOptions: EmployeeListDto[] = [];
  departmentSelectOptions: DepartmentDto[] = [];

  constructor(
    private readonly employeeClient: EmployeeClient,
    private readonly dialogRef: MatDialogRef<EmployeeAddComponent>,
    private readonly fb: FormBuilder
  ) {
    this.initForm();
  }

  initForm() {
    this.addEmployeeForm = this.fb.group({
      name: ['', [Validators.required, Validators.maxLength(100)]],
      position: ['', [Validators.required, Validators.maxLength(100)]],
      phone: ['', [Validators.required, Validators.maxLength(16)]],
      username: ['', [Validators.required, Validators.maxLength(100)]],
      password: [
        '',
        [
          Validators.required,
          Validators.minLength(8),
          Validators.maxLength(20),
        ],
      ],
      bossId: [''],
      departmentId: ['', [Validators.required]],
    });
  }

  cancel() {
    this.dialogRef.close(false);
  }

  submitForm() {
    this.employeeClient
      .employeePOST({
        name: this.addEmployeeForm.controls['name'].value,
        position: this.addEmployeeForm.controls['abbreviation'].value,
        phone: this.addEmployeeForm.controls['phone'].value,
        username: this.addEmployeeForm.controls['username'].value,
        password: this.addEmployeeForm.controls['password'].value,
        bossId: this.addEmployeeForm.controls['bossId'].value,
        departmentId: this.addEmployeeForm.controls['departmentId'].value,
      })
      .subscribe(() => {
        this.dialogRef.close(true);
      });
  }

  public errorHandling = (control: string, error: string) => {
    return this.addEmployeeForm.controls[control].hasError(error);
  };
}
<div class="container">
  <h3 mat-dialog-title>Add new employee</h3>

  <form [formGroup]="addEmployeeForm">
    <div class="content-container">
      <mat-form-field appearance="fill">
        <mat-label>Name</mat-label>
        <input matInput placeholder="Name" formControlName="name" />
        <mat-error *ngIf="errorHandling('name', 'required')">
          Name is required
        </mat-error>
        <mat-error *ngIf="errorHandling('name', 'maxlength')">
          Name can't be longer than 100 characters
        </mat-error>
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Abbreviation</mat-label>
        <input
          matInput
          placeholder="Abbreviation"
          formControlName="abbreviation"
        />
        <mat-error *ngIf="errorHandling('abbreviation', 'required')">
          Abbreviation is required
        </mat-error>
        <mat-error *ngIf="errorHandling('abbreviation', 'maxlength')">
          Abbreviation can't be longer than 5 characters
        </mat-error>
      </mat-form-field>

      <div mat-dialog-actions>
        <button
          mat-raised-button
          type="submit"
          color="primary"
          [disabled]="!addEmployeeForm.valid"
          [mat-dialog-close]="1"
          (click)="submitForm()"
        >
          Save
        </button>
        <button mat-button (click)="cancel()" tabindex="-1">Cancel</button>
      </div>
    </div>
  </form>
</div>
