import { Component, OnInit } from '@angular/core';
import {
  DepartmentClient,
  DepartmentDto,
} from 'src/app/core/api/app.generated';
import { MatDialog } from '@angular/material/dialog';
import { DepartmentAddComponent } from '../department-add/department-add.component';

@Component({
  selector: 'app-department-list',
  templateUrl: './department-list.component.html',
  styleUrls: ['./department-list.component.css'],
})
export class DepartmentListComponent implements OnInit {
  departments: DepartmentDto[] = [];

  columnsToDisplay = ['name', 'abbreviation'];

  constructor(
    private readonly departmentClient: DepartmentClient,
    private readonly dialog: MatDialog
  ) {}
  ngOnInit(): void {
    this.loadData();
  }

  loadData(): void {
    this.departmentClient.departmentAll().subscribe((data) => {
      this.departments = data;
    });
  }

  addDepartment() {
    const dialogRef = this.dialog.open(DepartmentAddComponent);

    dialogRef.afterClosed().subscribe((result) => {
      if (result) {
        this.loadData();
      }
    });
  }

  deleteDepartment(departmentId: number) {
    this.departmentClient.departmentDELETE(departmentId).subscribe(() => {
      this.loadData();
    });
  }
}
<mat-toolbar color="primary">
  <span>Department list</span>
  <span class="spacer"></span>
  Reload data:
  <button mat-icon-button (click)="loadData()">
    <mat-icon>refresh</mat-icon>
  </button>
</mat-toolbar>

<table mat-table [dataSource]="departments">
  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef>Name</th>
    <td mat-cell *matCellDef="let department">{{ department.name }}</td>
  </ng-container>
  <ng-container matColumnDef="abbreviation">
    <th mat-header-cell *matHeaderCellDef>Abbreviation</th>
    <td mat-cell *matCellDef="let department">{{ department.abbreviation }}</td>
  </ng-container>

  <ng-container matColumnDef="actions">
    <mat-header-cell *matHeaderCellDef>
      <button mat-icon-button (click)="addDepartment()">
        <mat-icon aria-label="Example icon-button with a heart icon"
          >add</mat-icon
        >
      </button>
    </mat-header-cell>

    <mat-cell *matCellDef="let row; let i = index">
      <button mat-icon-button color="accent">
        <mat-icon aria-label="Edit">edit</mat-icon>
      </button>

      <button mat-icon-button color="accent" (click)="deleteDepartment(row.id)">
        <mat-icon aria-label="Delete">delete</mat-icon>
      </button>
    </mat-cell>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="columnsToDisplay"></tr>
  <tr mat-row *matRowDef="let myRowData; columns: columnsToDisplay"></tr>
</table>
