import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { LoginClient } from 'src/app/api/app.generated';
import { AuthService } from 'src/app/shared/services/auth.service';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css'],
})
export class LoginPageComponent {
  loginForm!: FormGroup;
  loginValid: boolean = true;

  constructor(
    private readonly loginClient: LoginClient,
    private readonly fb: FormBuilder,
    private readonly auth: AuthService,
    private readonly router: Router
  ) {
    this.loginForm = this.fb.group({
      username: ['', [Validators.required, Validators.maxLength(100)]],
      password: ['', [Validators.required, Validators.minLength(8)]],
    });
  }

  submit() {
    if (this.loginForm.valid) {
      this.loginClient
        .login({
          username: this.loginForm.controls['username'].value,
          password: this.loginForm.controls['password'].value,
        })
        .subscribe(
          (result) => {
            this.loginValid = true;
            this.auth.setUser(result);
            this.router.navigate(['employees']);
          },
          (error) => {
            console.log(error);
            this.loginValid = false;
          }
        );
    }
  }

  public errorHandling = (control: string, error: string) => {
    return this.loginForm.controls[control].hasError(error);
  };
}
<div class="login-form-flex">
  <mat-card [style]="{ 'min-width': '500px', 'align-items': 'center' }">
    <mat-card-title class="pt-1 pb-3"
      >Enter your username and password</mat-card-title
    >

    <p class="hint">Username: "bob" Password: "password"</p>

    <mat-card-content>
      <form [formGroup]="loginForm" (ngSubmit)="submit()">
        <p>
          <mat-form-field appearance="fill">
            <mat-label>Username</mat-label>
            <input matInput placeholder="Username" formControlName="username" />
            <mat-error *ngIf="errorHandling('username', 'required')">
              Username is required
            </mat-error>
          </mat-form-field>
        </p>

        <p>
          <mat-form-field appearance="fill">
            <mat-label>Password</mat-label>
            <input
              type="password"
              matInput
              placeholder="Password"
              formControlName="password"
            />
            <mat-error *ngIf="errorHandling('password', 'required')">
              Password is required
            </mat-error>
          </mat-form-field>
        </p>

        <p>
          <mat-error *ngIf="!loginValid"> Invalid credentials </mat-error>
        </p>

        <div class="button-flex-container">
          <button color="primary" mat-raised-button>Submit</button>
        </div>
      </form>
    </mat-card-content>
  </mat-card>
</div>
