import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { EmployeeClient, EmployeeListDto } from 'src/app/api/app.generated';
import { EmployeeAddComponent } from '../employee-add/employee-add.component';
import { EmployeeEditComponent } from '../employee-edit/employee-edit.component';
import {
  MatSnackBar,
  MatSnackBarHorizontalPosition,
  MatSnackBarVerticalPosition,
} from '@angular/material/snack-bar';

@Component({
  selector: 'app-employee-list',
  templateUrl: './employee-list.component.html',
  styleUrls: ['./employee-list.component.css'],
})
export class EmployeeListComponent {
  employees: EmployeeListDto[] = [];

  columnsToDisplay = ['name', 'position', 'phone', 'departmentName', 'actions'];
  horizontalPosition: MatSnackBarHorizontalPosition = 'end';
  verticalPosition: MatSnackBarVerticalPosition = 'top';

  constructor(
    private readonly employeeClient: EmployeeClient,
    private readonly dialog: MatDialog,
    private readonly snackBar: MatSnackBar
  ) {}
  ngOnInit(): void {
    this.loadData();
  }

  loadData(): void {
    this.employeeClient.employeeAll().subscribe((data) => {
      this.employees = data;
    });
  }

  addEmployee() {
    const dialogRef = this.dialog.open(EmployeeAddComponent);
    dialogRef.afterClosed().subscribe((result) => {
      this.snackBar.open('Success', 'Employee create success', {
        duration: 500,
        horizontalPosition: this.horizontalPosition,
        verticalPosition: this.verticalPosition,
      });
      this.loadData();
    });
  }

  deleteEmployee(employeeId: number) {
    this.employeeClient.employeeDELETE(employeeId).subscribe(() => {
      this.snackBar.open('Success', 'Employee delete success', {
        duration: 500,
        horizontalPosition: this.horizontalPosition,
        verticalPosition: this.verticalPosition,
      });
      this.loadData();
    });
  }

  editEmployee(employeeId: number) {
    const dialogRef = this.dialog.open(EmployeeEditComponent, {
      data: { id: employeeId },
    });

    dialogRef.afterClosed().subscribe((result) => {
      if (result) {
        this.snackBar.open('Success', 'Employee edit success', {
          duration: 500,
          horizontalPosition: this.horizontalPosition,
          verticalPosition: this.verticalPosition,
        });
        this.loadData();
      }
    });
  }
}
<mat-toolbar color="primary">
  <span>Employee list</span>
  <span class="spacer"></span>
  Reload data:
  <button mat-icon-button (click)="loadData()">
    <mat-icon>refresh</mat-icon>
  </button>
</mat-toolbar>

<table mat-table [dataSource]="employees">
  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef>Name</th>
    <td mat-cell *matCellDef="let employee">{{ employee.name }}</td>
  </ng-container>
  <ng-container matColumnDef="position">
    <th mat-header-cell *matHeaderCellDef>Position</th>
    <td mat-cell *matCellDef="let employee">{{ employee.position }}</td>
  </ng-container>
  <ng-container matColumnDef="phone">
    <th mat-header-cell *matHeaderCellDef>Phone</th>
    <td mat-cell *matCellDef="let employee">{{ employee.phone }}</td>
  </ng-container>
  <ng-container matColumnDef="departmentName">
    <th mat-header-cell *matHeaderCellDef>Department</th>
    <td mat-cell *matCellDef="let employee">{{ employee.departmentName }}</td>
  </ng-container>

  <ng-container matColumnDef="actions">
    <th mat-header-cell *matHeaderCellDef>
      <button mat-icon-button color="primary" (click)="addEmployee()">
        <mat-icon>add</mat-icon>
      </button>
    </th>

    <td mat-cell *matCellDef="let row; let i = index">
      <button mat-icon-button color="accent" (click)="editEmployee(row.id)">
        <mat-icon aria-label="Edit">edit</mat-icon>
      </button>

      <button mat-icon-button color="accent" (click)="deleteEmployee(row.id)">
        <mat-icon aria-label="Delete">delete</mat-icon>
      </button>
    </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="columnsToDisplay"></tr>
  <tr mat-row *matRowDef="let myRowData; columns: columnsToDisplay"></tr>
</table>
