import { Component, Inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { DepartmentClient, DepartmentDto } from 'src/app/api/app.generated';

@Component({
  selector: 'app-department-edit',
  templateUrl: './department-edit.component.html',
  styleUrls: ['./department-edit.component.css'],
})
export class DepartmentEditComponent {
  editDepartmentForm!: FormGroup;

  constructor(
    private readonly departmentClient: DepartmentClient,
    private readonly dialogRef: MatDialogRef<DepartmentEditComponent>,
    @Inject(MAT_DIALOG_DATA) public data: DepartmentDto,
    private readonly fb: FormBuilder
  ) {
    this.initForm();
  }

  initForm() {
    this.editDepartmentForm = this.fb.group({
      name: [this.data.name, [Validators.required, Validators.maxLength(100)]],
      abbreviation: [
        this.data.abbreviation,
        [Validators.required, Validators.maxLength(5)],
      ],
    });
  }

  cancel() {
    this.dialogRef.close();
  }

  submitForm() {
    this.departmentClient
      .departmentPUT(this.data.id!, {
        name: this.editDepartmentForm.controls['name'].value,
        abbreviation: this.editDepartmentForm.controls['abbreviation'].value,
      })
      .subscribe(() => {
        this.dialogRef.close(true);
      });
  }

  public errorHandling = (control: string, error: string) => {
    return this.editDepartmentForm.controls[control].hasError(error);
  };
}
<div class="container">
  <h3 mat-dialog-title>Edit department</h3>

  <form [formGroup]="editDepartmentForm">
    <div class="content-container">
      <mat-form-field appearance="fill">
        <mat-label>Name</mat-label>
        <input matInput placeholder="Name" formControlName="name" />
        <mat-error *ngIf="errorHandling('name', 'required')">
          Name is required
        </mat-error>
        <mat-error *ngIf="errorHandling('name', 'maxlength')">
          Name can't be longer than 100 characters
        </mat-error>
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Abbreviation</mat-label>
        <input
          matInput
          placeholder="Abbreviation"
          formControlName="abbreviation"
        />
        <mat-error *ngIf="errorHandling('abbreviation', 'required')">
          Abbreviation is required
        </mat-error>
        <mat-error *ngIf="errorHandling('abbreviation', 'maxlength')">
          Abbreviation can't be longer than 5 characters
        </mat-error>
      </mat-form-field>

      <div mat-dialog-actions>
        <button
          mat-raised-button
          type="submit"
          color="primary"
          [disabled]="!editDepartmentForm.valid"
          [mat-dialog-close]="1"
          (click)="submitForm()"
        >
          Save
        </button>
        <button mat-button (click)="cancel()" tabindex="-1">Cancel</button>
      </div>
    </div>
  </form>
</div>
