import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import {
  DepartmentClient,
  DepartmentCreateOrUpdateDto,
} from 'src/app/api/app.generated';

@Component({
  selector: 'app-department-add',
  templateUrl: './department-add.component.html',
  styleUrls: ['./department-add.component.css'],
})
export class DepartmentAddComponent {
  addDepartmentForm!: FormGroup;

  constructor(
    private readonly departmentClient: DepartmentClient,
    public dialogRef: MatDialogRef<DepartmentAddComponent>,
    public fb: FormBuilder
  ) {}

  initForm() {
    this.addDepartmentForm = this.fb.group({
      name: ['', [Validators.required, Validators.maxLength(100)]],
      abbreviation: ['', Validators.required, Validators.maxLength(5)],
    });
  }

  cancel() {
    this.dialogRef.close(false);
  }

  submitForm() {
    this.departmentClient
      .departmentPOST({
        name: this.addDepartmentForm.controls['name'].value,
        abbreviation: this.addDepartmentForm.controls['abbreviation'].value,
      })
      .subscribe(() => {
        this.dialogRef.close(true);
      });
  }

  public errorHandling = (control: string, error: string) => {
    return this.addDepartmentForm.controls[control].hasError(error);
  };
}
<div class="container">
  <h3 mat-dialog-title>Add new department</h3>

  <form [formGroup]="addDepartmentForm" (ngSubmit)="submitForm()" novalidate>
    <mat-form-field>
      <mat-label>Name</mat-label>
      <input
        matInput
        placeholder="Name"
        formControlName="name"
        class="full-width"
      />
      <mat-error *ngIf="errorHandling('name', 'required')">
        Name is required
      </mat-error>
      <mat-error *ngIf="errorHandling('name', 'maxLength')">
        Name can't be longer than 100 characters
      </mat-error>
    </mat-form-field>

    <mat-form-field>
      <mat-label>Abbreviation</mat-label>
      <input
        matInput
        placeholder="Abbreviation"
        formControlName="abbreviation"
        class="full-width"
      />
      <mat-error *ngIf="errorHandling('abbreviation', 'required')">
        Abbreviation is required
      </mat-error>
      <mat-error *ngIf="errorHandling('abbreviation', 'maxLength')">
        Abbreviation can't be longer than 5 characters
      </mat-error>
    </mat-form-field>

    <div mat-dialog-actions>
      <button
        mat-button
        [disabled]="!addDepartmentForm.valid"
        [mat-dialog-close]="1"
        (click)="submitForm()"
      >
        Save
      </button>
      <button mat-button (click)="cancel()" tabindex="-1">Cancel</button>
    </div>
  </form>
</div>
